import { api } from "../../utils/api";
import React, { useState } from 'react';

function Main(props) {

    const [userName, setUserName] = React.useState('');
    const [userDescription, setUserDescription] = React.useState('');
    const [userAvatar, setUserAvatar] = React.useState('');
    const [cards, setCards] = React.useState([])

    React.useEffect(() => {
        api.getUserData()
          .then(result => {
            setUserName(result.name);
            setUserDescription(result.about);
            setUserAvatar(result.avatar);
          })
          .catch(err => {
            console.log(err);
          })
          api.getCardsData()
          .then(result => {
            setCards(result);
          })
          .catch(err => {
            console.log(err);
          })
    }, []);

    // React.useEffect(() => {
    //     api.getCardsData()
    //       .then(result => {
    //         setCards(result);
    //       })
    //       .catch(err => {
    //         console.log(err);
    //       })
    // }, []);

  return (
    <div className="content">
        <section className="profile">
            <div className="profile__info">
                <img className="profile__avatar" src={userAvatar} alt="Аватар"/>
                <div onClick={props.onEditAvatar} className="profile__edit"/>
                <h1 className="profile__user-name">{userName}</h1>
                <p className="profile__description">{userDescription}</p>
                <button onClick={props.onEditProfile} className="button profile__edit-button" type="button"/>
            </div>
            <button onClick={props.onAddPlace} className="button profile__add-button" type="button"/>
        </section>
        <section className="elements"/>
        <template className="template">
            <div className="element" key={item.id}>
                { cards.map((item) => (
                <><img className="element__picture" src={item.link} alt=" " /><button className="element__remove" type="button" /><div className="element__group">
                        <h2 className="element__name">{item.name}</h2>
                        <div className="element__likes">
                            <button className="element__like" type="button" />
                            <h3 className="element__like-number">{item.likes.length}</h3>
                        </div>
                    </div></>
                    )) }
            </div>
    </template>
    </div>
  );
}

export default Main;
