function PopupWithForm({name,title,children,buttonText,isOpen,onClose}) {
  return(
    <div className={ isOpen ? `popup popup_${name} popup_opened` : `popup popup_${name}`}>
      <div className="popup__content">
        <h3 className="popup__title">{title}</h3>
        <button className={ onClose ? "popup button popup__close" : "button popup__close" } type="button"></button>
        <form className="popup__form" name={name} noValidate>
          {children}
          <button className="popup__save" type="submit" >{buttonText}</button>
        </form>
      </div>
    </div>
  );
}

export default PopupWithForm;
