import { useState } from 'react';
import './Main.css';


function Main() {

const[avatar,setAvatar] = useState();

function handleEditAvatarClick(value){
    setAvatar(value);
    const button = document.querySelector('.popup_avatar');
    button.classList.add('popup_opened')
}

  return (
    <div className="content">
        <section className="profile">
            <div className="profile__info">
                <img className="profile__avatar" src="{avatar}" alt="Аватар"/>
                <div className="profile__edit"/>
                <h1 className="profile__user-name">Жак-Ив Кусто</h1>
                <p className="profile__description">Исследователь океанов</p>
                <button className="button profile__edit-button" type="button"/>
            </div>
            <button className="button profile__add-button" type="button"/>
        </section>
        <section className="elements"/>
        <div onClick={handleEditAvatarClick} className="popup popup_avatar">
            <div className="popup__content popup__content_avatar">
                <h3 className="popup__title">Обновить аватар</h3>
                <button type="button" className="button popup__close"></button>
                <form className="popup__form popup__form_avatar" name="form" noValidate>
                    <div className="popup__field">
                        <input id="photo-input" type="url" className="popup__input popup__input_type_photo" value="" name="avatar" minLength="2" maxLength="140" required/>
                        <span className="error" id="photo-input-error"></span>
                    </div>
                    <button type="submit" className="popup__save popup__save_confirm popup__save_avatar">Сохранить</button>
                </form>
            </div>
        </div>
    </div>
  );
}

export default Main;
