import Footer from '../footer/Footer';
import Header from '../header/Header';
import Main from '../main/Main';
import PopupWithForm from '../popupWithForm/PopupWithForm';
import { useState } from 'react';


function App() {
  // // const[avatar,setAvatar] = useState('');

  // // function onEditProfile(value){
  // //   setAvatar(value);
  // }

  const [isOpen, setIsOpen] = useState(false)

  function handleEditAvatarClick(){
    const popup = document.querySelector('.popup_avatar');
    popup.classList.add('popup_opened');
  }

  function handleEditProfileClick(){
    const popup = document.querySelector('.popup_profile-form');
    popup.classList.add('popup_opened');
  }

  function handleAddPlaceClick(){
    const popup = document.querySelector('.popup_place-profile');
    popup.classList.add('popup_opened');
  }

  return (
    <div className="page">
    <Header/>
    <Main
      onEditAvatar={handleEditAvatarClick}
      onAddPlace={handleAddPlaceClick}
      onEditProfile={handleEditProfileClick}

    />
    <Footer/>
    <PopupWithForm
      name = "_avatar"
      title= "Обновить аватар"
      buttonText = "Сохранить">
      <div className="popup__field">
        <input  id="photo-input" type="url" className="popup__input popup__input_type_photo" value="" name="avatar" minLength="2" maxLength="140" required/>
        <span className="error" id="photo-input-error"></span>
      </div>
    </PopupWithForm>
    <PopupWithForm
      name = "_profile-form"
      title= "Редактировать профиль"
      buttonText = "Сохранить">
      <div className="popup__field">
        <input id="name-input" type="text" className="popup__input popup__input_type_name" value="" name="name" minLength="2" maxLength="40" required/>
        <span className="error" id="name-input-error"></span>
      </div>
      <div className="popup__field">
        <input id="job-input" type="text" className="popup__input popup__input_type_job" value="" name="about" minLength="2" maxLength="200" required/>
        <span className="error" id="job-input-error"></span>
      </div>
    </PopupWithForm>
    <PopupWithForm
      name = "_place-profile"
      title= "Новое место"
      buttonText = "Создать">
      <div className="popup__field">
        <input id="place-input" type="text" className="popup__input popup__input_place_name" placeholder="Название" name="name" minLength="2" maxLength="30" required/>
        <span className="error" id="place-input-error"></span>
      </div>
      <div className="popup__field">
        <input id="link-input" type="url" className="popup__input popup__input_place_link" placeholder="Ссылка на картинку" name="link" required/>
        <span className="error" id="link-input-error"></span>
      </div>
    </PopupWithForm>
    <PopupWithForm
      name = "_place-delete"
      title= ""
      buttonText = "Вы уверены?">
      <div className="popup__field">
        <input id="place-input" type="text" className="popup__input popup__input_place_name" placeholder="Название" name="name" minLength="2" maxLength="30" required/>
        <span className="error" id="place-input-error"></span>
      </div>
      <div className="popup__field">
        <input id="link-input" type="url" className="popup__input popup__input_place_link" placeholder="Ссылка на картинку" name="link" required/>
        <span className="error" id="link-input-error"></span>
      </div>
    </PopupWithForm>


    <template className="template">
        <div className="element">
            <img className="element__picture" src=" " alt=" "/>
            <button className="element__remove" type="button"></button>
            <div className="element__group">
                <h2 className="element__name"></h2>
                <div className="element__likes">
                    <button className="element__like" type="button"></button>
                    <h3 className="element__like-number"></h3>
                </div>
            </div>
        </div>
    </template>

    <div className="popup popup_place-delete">
        <div className="popup__content popup__content_place_delete">
           <h3 className="popup__title">Вы уверены?</h3>
           <button type="button" className="button popup__close"></button>
           <form className="popup__form popup__form-confirm" name="form" noValidate>
               <button type="submit" className="popup__save popup__save_confirm popup__save_delete">Да</button>
           </form>
       </div>
   </div>
  </div>
  );
}

export default App;
