import Footer from '../footer/Footer';
import Header from '../header/Header';
import Main from '../main/Main';
import PopupWithForm from '../popupWithForm/PopupWithForm';
//import { useState } from 'react';


function App() {
  // // const[avatar,setAvatar] = useState('');

  // // function onEditProfile(value){
  // //   setAvatar(value);
  // }

  const [isOpen, setIsOpen] = React.useState(false)

  function handleEditAvatarClick(){
    const popup = document.querySelector('.popup_avatar');
    popup.classList.add('popup_opened');
}

  return (
    <div className="page">
    <Header/>
    <Main
      onEditProfile={handleEditAvatarClick}
    />
    <Footer/>
    <PopupWithForm
      name = "_avatar"
      title= "Обновить аватар"
      buttonText = "Сохранить">
      <div className="popup__field">
        <input  id="photo-input" type="url" className="popup__input popup__input_type_photo" value="" name="avatar" minLength="2" maxLength="140" required/>
        <span className="error" id="photo-input-error"></span>
      </div>
    </PopupWithForm>

    <template className="template">
        <div className="element">
            <img className="element__picture" src=" " alt=" "/>
            <button className="element__remove" type="button"></button>
            <div className="element__group">
                <h2 className="element__name"></h2>
                <div className="element__likes">
                    <button className="element__like" type="button"></button>
                    <h3 className="element__like-number"></h3>
                </div>
            </div>
        </div>
    </template>

    <div className="popup popup_place-image">
        <div className="popup__content popup__content_place_image">
        <button type="button" className="button popup__close popup__close_image"></button>
            <figure className="popup__figure">
                <img className="popup__image" scr="" alt=""/>
                <figcaption className="popup__caption"></figcaption>
            </figure>
        </div>
    </div>
    <div className="popup popup_place-delete">
        <div className="popup__content popup__content_place_delete">
           <h3 className="popup__title">Вы уверены?</h3>
           <button type="button" className="button popup__close"></button>
           <form className="popup__form popup__form-confirm" name="form" noValidate>
               <button type="submit" className="popup__save popup__save_confirm popup__save_delete">Да</button>
           </form>
       </div>
   </div>
  </div>
  );
}

export default App;
