import { useState } from 'react';


function Main() {

// const[avatar,setAvatar] = useState();

// function onInput(value){
//     setAvatar(value);
// }

function handleEditAvatarClick(){
    const popup = document.querySelector('.popup_avatar');
    popup.classList.add('popup_opened');
}

function handleEditProfileClick(){
    const popup = document.querySelector('.popup_profile-form');
    popup.classList.add('popup_opened');
}

  return (
    <div className="content">
        <section className="profile">
            <div className="profile__info">
                <img className="profile__avatar" src="" alt="Аватар"/>
                <div onClick={handleEditAvatarClick} className="profile__edit"/>
                <h1 className="profile__user-name">Жак-Ив Кусто</h1>
                <p className="profile__description">Исследователь океанов</p>
                <button onClick={handleEditProfileClick} className="button profile__edit-button" type="button"/>
            </div>
            <button className="button profile__add-button" type="button"/>
        </section>
        <section className="elements"/>
        <div className="popup popup_avatar">
            <div className="popup__content popup__content_avatar">
                <h3 className="popup__title">Обновить аватар</h3>
                <button type="button" className="button popup__close"></button>
                <form className="popup__form popup__form_avatar" name="form" noValidate>
                    <div className="popup__field">
                        <input  id="photo-input" type="url" className="popup__input popup__input_type_photo" value="" name="avatar" minLength="2" maxLength="140" required/>
                        <span className="error" id="photo-input-error"></span>
                    </div>
                    <button type="submit" className="popup__save popup__save_confirm popup__save_avatar">Сохранить</button>
                </form>
            </div>
        </div>
        <div className="popup popup_profile-form">
            <div className="popup__content">
                <h3 className="popup__title">Редактировать профиль</h3>
                <button type="button" className="button popup__close"></button>
                <form className="popup__form" name="form" noValidate>
                    <div className="popup__field">
                        <input id="name-input" type="text" className="popup__input popup__input_type_name" value="" name="name" minLength="2" maxLength="40" required/>
                        <span className="error" id="name-input-error"></span>
                    </div>
                    <div className="popup__field">
                        <input id="job-input" type="text" className="popup__input popup__input_type_job" value="" name="about" minLength="2" maxLength="200" required/>
                        <span className="error" id="job-input-error"></span>
                    </div>
                    <button type="submit" className="popup__save popup__save_profile">Сохранить</button>
                </form>
            </div>
        </div>
    </div>
  );
}

export default Main;
