import React from 'react';
import PopupWithForm from "./PopupWithForm";
import { CurrentUserContext } from '../contexts/CurrentUserContext'

function AddPlacePopup({isOpen,onClose,onAddPlace}) {

    const [name, setName] = React.useState('')
    const [link, setLink] = React.useState('')

    function handleNamePut(e) {
        setName(e.target.value);
      }

      function handleLinkPut(e) {
        setLink(e.target.value);
      }


    function handleSubmit(e){
        e.preventDefault();
        onAddPlace({
          name,
          link
        });
    }

    return(
        <PopupWithForm
        name = "place-profile"
        title= "Новое место"
        buttonText = "Создать"
        isOpen={isOpen}
        onClose={onClose}
        onSubmit={handleSubmit}
        >
        <div className="popup__field">
          <input id="place-input" type="text" className="popup__input popup__input_place_name" onChange={handleNamePut} placeholder="Название" name="name" minLength="2" maxLength="30" required/>
          <span className="error" id="place-input-error"></span>
        </div>
        <div className="popup__field">
          <input id="link-input" type="url" className="popup__input popup__input_place_link" onChange={handleLinkPut} placeholder="Ссылка на картинку" name="link" required/>
          <span className="error" id="link-input-error"></span>
        </div>
      </PopupWithForm>
    );
  }

  export default AddPlacePopup;
