import { api } from "../../utils/api";
import React, { useState } from 'react';

function Main(props) {

    const [userName, setUserName] = React.useState('');
    const [userDescription, setUserDescription] = React.useState('');
    const [userAvatar, setUserAvatar] = React.useState('');

    React.useEffect(() => {
        api.getUserData()
          .then(result => {
            setUserName(result.name);
            setUserDescription(result.about);
            setUserAvatar(result.avatar);
          })
          .catch(err => {
            console.log(err);
          })
    }, []);

  return (
    <div className="content">
        <section className="profile">
            <div className="profile__info">
                <img className="profile__avatar" style={{ backgroundImage: `url(${userAvatar})` }} alt="Аватар"/>
                <div onClick={props.onEditAvatar} className="profile__edit"/>
                <h1 className="profile__user-name">{result.name}</h1>
                <p className="profile__description">{result.avatar}</p>
                <button onClick={props.onEditProfile} className="button profile__edit-button" type="button"/>
            </div>
            <button onClick={props.onAddPlace} className="button profile__add-button" type="button"/>
        </section>
        <section className="elements"/>

    </div>
  );
}

export default Main;
