import Success from '../images/Union.svg';
import Reject from '../images/Rectangle.svg';

function InfoTooltip({isOpen,name,onClose}) {
  return (
    <div className={ isOpen ? `popup popup_${name} popup_opened` : `popup popup_${name}`}>
      <div className={`popup__content popup__content_${name}`}>
      <button className="button popup__close" onClick={onClose} type="button"></button>
        <img  src={Success} alt="результат регистрации" />
        <p className="popup__title popup__title_signup">Вы успешно зарегистрировались!
          {/* {props.auth ? "Вы успешно зарегистрировались!" : "Что-то пошло не так! Попробуйте ещё раз."} */}
        </p>
      </div>
    </div>
  )
}

export default InfoTooltip;
